import React, { Component } from "react";
import "../style/style.css";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";
import { connect } from "react-redux";
import { fetchArticleDetails } from "./fetchData";

export class pagination extends Component {
  constructor() {
    super();
    this.state = {
      currentPage: 1,
      todosPerPage: 30,
      optionsPerPage: [
        {
          text: "Option 1",
          value: 10
        },
        {
          text: "Option 2",
          value: 20
        },
        {
          text: "Option 3",
          value: 30
        }
      ],
      valuePerPage: ""
    };
  }
  componentDidMount() {
    this.props.fetchArticleDetails();
  }
  handleClick = event => {
    this.setState({
      currentPage: Number(event.target.id)
    });
  };
  onChangeSelectValue = e => {
    this.setState({
      valuePerPage: e.target.value
    });
  };

  render() {
    console.log(this.state.optionsPerPage);
    const { error, loading, comments } = this.props;
    const { valuePerPage } = this.state;

    if (error) {
      return <div>Error! {error.message}</div>;
    }

    if (loading) {
      return <div>Loading...</div>;
    }
    const { currentPage, todosPerPage } = this.state;

    // Logic for displaying todos
    const indexOfLastTodo = currentPage * `${valuePerPage}`;
    const indexOfFirstTodo = indexOfLastTodo - `${valuePerPage}`;
    const currentTodos = comments.slice(indexOfFirstTodo, indexOfLastTodo);

    const renderTodos = currentTodos.map(item => {
      return (
        <tbody key={item.id}>
          <tr>
            <td>{item.id}</td>
            <td>{item.name} </td>
            <td>{item.email}</td>
          </tr>
        </tbody>
      );
    });
    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(comments.length / todosPerPage); i++) {
      pageNumbers.push(i);
    }
    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <ul className="pagination">
          <li
            key={number}
            id={number}
            onClick={this.handleClick}
            className="page-link"
          >
            {number}
          </li>
        </ul>
      );
    });

    return (
      <div className="container">
        <Form.Group controlId="exampleForm.ControlSelect1">
          <Form.Label>Rodyti</Form.Label>
          <Form.Control as="select" onChange={this.onChangeSelectValue}>
            <option>{this.state.optionsPerPage.value[1]}</option>
          </Form.Control>
        </Form.Group>
        <Table striped bordered hover variant="dark">
          <thead>
            <tr>
              <th>ID</th>
              <th>Komentarai</th>
              <th>Email</th>
            </tr>
          </thead>
          {renderTodos}
        </Table>
        <ul>{renderPageNumbers}</ul>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    comments: state.comments,
    loading: state.loading,
    error: state.error
  };
};

export default connect(mapStateToProps, { fetchArticleDetails })(pagination);
