import { actionType } from "../actions";

const initialState = {
  comments: []
};

export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionType.FETCH_DATA:
      return {
        ...state,
        comments: action.payload
      };
    default:
      return state;
  }
};
