import React, { Component } from "react";
import "../style/style.css";
import Table from "react-bootstrap/Table";
import { connect } from "react-redux";
import { fetchArticleDetails } from "./fetchData";

export class pagination extends Component {
  constructor() {
    super();
    this.state = {
      currentPage: 1,
      todosPerPage: 50,
      valuePerPage: 40,
      sortedColumns: "",
      direction: {
        id: "asc"
      }
    };
  }
  componentDidMount() {
    this.props.fetchArticleDetails();
  }
  handleClick = event => {
    this.setState({
      currentPage: Number(event.target.id)
    });
  };

  handleChange = e => {
    this.setState({ todosPerPage: e.target.value });
  };

  sort = key => {
    const sortComments = this.props.comments;
    this.setState({
      sortedColumns: sortComments.sort((a, b) =>
        this.state.direction[key] === "asc"
          ? parseFloat(a[key]) - parseFloat(b[key])
          : parseFloat(b[key]) - parseFloat(a[key])
      )
    });
  };

  render() {
    console.log(this.state.valuePerPage);
    const { error, loading, comments } = this.props;

    if (error) {
      return <div>Error! {error.message}</div>;
    }

    if (loading) {
      return <div>Loading...</div>;
    }
    const { currentPage, todosPerPage } = this.state;

    // Logic for displaying todos
    const indexOfLastTodo = currentPage * todosPerPage;
    const indexOfFirstTodo = indexOfLastTodo - todosPerPage;
    const currentTodos = comments.slice(indexOfFirstTodo, indexOfLastTodo);

    const renderTodos = currentTodos.map(item => {
      return (
        <tbody key={item.id}>
          <tr>
            <td>{item.id}</td>
            <td>{item.name} </td>
            <td>{item.email}</td>
          </tr>
        </tbody>
      );
    });
    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(comments.length / todosPerPage); i++) {
      pageNumbers.push(i);
    }
    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <ul className="pagination">
          <li
            key={number}
            id={number}
            onClick={this.handleClick}
            className="page-link"
          >
            {number}
          </li>
        </ul>
      );
    });

    return (
      <div className="container">
        <label>Rodyti po</label>
        <select value={this.state.todosPerPage} onChange={this.handleChange}>
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="30">30</option>
          <option value="40">40</option>
          <option value="40">50</option>
        </select>
        <Table striped bordered hover variant="dark">
          <thead>
            <tr>
              <th onClick={this.sort}>ID</th>
              <th>Komentarai</th>
              <th>Email</th>
            </tr>
          </thead>
          {renderTodos}
        </Table>
        <ul>{renderPageNumbers}</ul>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    comments: state.comments,
    loading: state.loading,
    error: state.error
  };
};

export default connect(mapStateToProps, { fetchArticleDetails })(pagination);
