import {
  FETCH_PRODUCTS_PENDING,
  FETCH_PRODUCTS_SUCCESS,
  FETCH_PRODUCTS_ERROR
} from "../actions";
const initialState = {
  comments: []
};

export const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionType.FETCH_DATA:
      return {
        ...state,
        comments: action.payload
      };
    default:
      return state;
  }
};
